mapper.select:
SELECT * FROM test_scenario_steps 
WHERE scenario_id = #{scenarioId} 
ORDER BY order_index ASC

mapper.insert:
INSERT INTO test_scenario_steps (
    scenario_id, parent_id, type, name, reference_case_id, 
    control_logic, data_overrides, order_index, created_at, updated_at
)
VALUES (
    #{scenarioId}, #{parentId}, #{type}, #{name}, #{referenceCaseId}, 
    #{controlLogic}, #{dataOverrides}, #{orderIndex}, #{createdAt}, #{updatedAt}
)
